import React, { useState, useCallback } from 'react'
import './UserProfile.css'
import { Card, CardContent, CardActions, Button, makeStyles } from '@material-ui/core'
import avatarDefault from 'assets/images/logo.png'
import FormInput from 'components/common/input/FormInput'
import UploadImage from 'components/common/upload/UploadImage'
import { updateProfileUser } from 'services/userService'
import { useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom'

const useStyles = makeStyles({
  root: {
    maxWidth: 600
  },
  avatar: {
    backgroundColor: '#fff'
  },
  media: {
    height: 140
  }
})

function UserProfile() {
  const classes = useStyles()
  const history = useHistory()
  const { userInfo } = useSelector((state) => state.userReducers)
  const [userName, setUserName] = useState('')
  const [phone, setPhone] = useState('')
  const [avatar, setAvatar] = useState('')
  const [error, setError] = useState({ message: '', status: false })

  // handle update profile
  const handleUpdateProfile = useCallback(() => {
    updateProfileUser({ userName, phone, avatar })
      .then((res) => {
        if (res.status === 1) {
          history.push('/')
        }
      })
      .catch((error) => {
        setError((prevState) => ({
          ...prevState,
          message: error.message,
          status: true
        }))
      })
      .finally(() => {})
  }, [userName, phone, avatar])

  return (
    <div className="pf__container">
      {error.status && <span>{error.message}</span>}
      <Card className={classes.root}>
        <div className="pf__avatar">
          <img src={(userInfo && userInfo.avatar) || avatarDefault} alt="my-avatar" />
          <UploadImage buttonText="Upload avatar" setImage={setAvatar} />
        </div>
        <CardContent>
          <form noValidate autoComplete="off" style={{ display: 'flex', flexDirection: 'column' }}>
            <FormInput
              label="Email"
              type="email"
              name="email"
              defaultValue={userInfo && userInfo.email}
              disabled={true}
            />
            <FormInput
              label="UserName"
              type="text"
              name="userName"
              defaultValue={userInfo && userInfo.username}
              setData={setUserName}
            />
            <FormInput
              label="Phone"
              type="phone"
              name="phone"
              defaultValue={(userInfo && userInfo.phone) || ''}
              setData={setPhone}
            />
          </form>
        </CardContent>
        <CardActions className="pf__actions">
          <Button size="small" variant="contained" color="primary" onClick={handleUpdateProfile}>
            Update profile
          </Button>
        </CardActions>
      </Card>
    </div>
  )
}

export default UserProfile
