import { initialState } from "../states/post";
import { getType, getPosts, createPost } from "../actions/postAction";
import { modalState } from "redux/states/modal";

const getAllPosts = (state = initialState.posts, action) => {
  switch (action.type) {
    case getType(getPosts.getPostsRequest):
      return {
        ...state,
        isLoading: true,
      };
    case getType(getPosts.getPostsSuccess):
      return {
        ...state,
        isLoading: true,
        data: action.payload,
      };
    case getType(getPosts.getPostsFailure):
      return {
        ...state,
        isLoading: false,
        data: [],
      };
    default:
      return state;
  }
};

const createNewPost = (state = modalState.modal, action) => {
  switch (action.type) {
    case getType(createPost.createPostRequest):
      return {
        ...state,
        isOpen: true,
      };
    case getType(createPost.createPostSuccess):
      return {
        ...state,
        isOpen: false,
      };
    case getType(createPost.createPostFailure):
      return {
        ...state,
        isOpen: true,
      };
    default:
      return state;
  }
};

export { getAllPosts, createNewPost };
