import { getPosts, getPostById } from 'api/api'

const getAllPost = async () => {
  try {
    const res = await getPosts()
    return res.data || []
  } catch (error) {
    throw error
  }
}

const getPostDetail = async (postId) => {
  try {
    const res = await getPostById(postId)
    return res.data || {}
  } catch (error) {
    throw error
  }
}

export { getAllPost, getPostDetail }
