import { takeLatest, put, call, actionChannel, take } from "redux-saga/effects";
import * as actions from "./../actions/postAction";
import * as api from "./../../api/api";

function* getPosts() {
  try {
    const res = yield call(api.getPosts);
    if (res.data.length) {
      yield put(actions.getPosts.getPostsSuccess(res.data));
    }
  } catch (error) {
    yield put(actions.getPosts.getPostsFailure(error));
  }
}

function* createPost(data) {
  try {
    const res = yield call(api.createPost(data));
    if (res) {
      yield put(actions.createPost.createPostSuccess(res.data));
    }
  } catch (error) {
    yield put(actions.createPost.createPostFailure(error));
  }
}

function* mySagas() {
  // yield takeLatest(actions.getPosts.getPostsRequest, getPosts);
  const requestGetPostsTask = yield actionChannel(
    actions.getPosts.getPostsRequest
  );
  const requestCreatePostTask = yield actionChannel(
    actions.createPost.createPostRequest
  );
  while (true) {
    const payloadGetPosts = yield take(requestGetPostsTask);
    const payloadCreatePost = yield take(requestCreatePostTask);
    console.log(payloadGetPosts, payloadCreatePost);
    yield call(getPosts, payloadGetPosts);
    yield call(createPost, payloadCreatePost);
  }
  // yield takeLatest(actions.createPost.createPostRequest, createPost);
}

export default mySagas;
