import React, { useState, useCallback } from 'react'
import './UserProfile.css'
import { Card, CardContent, CardActions, Button, makeStyles } from '@material-ui/core'
import avatar from 'assets/images/logo.png'
import FormInput from 'components/common/input/FormInput'
import UploadImage from 'components/common/upload/UploadImage'
import { updateProfileUser } from 'services/userService'
import { useSelector } from 'react-redux'

const useStyles = makeStyles({
  root: {
    maxWidth: 600
  },
  avatar: {
    backgroundColor: '#fff'
  },
  media: {
    height: 140
  }
})

function UserProfile() {
  const classes = useStyles()
  const { userInfo } = useSelector((state) => state.userReducers)
  const [displayName, setDisplayName] = useState('')
  const [phoneNumber, setPhoneNumber] = useState('')
  const [error, setError] = useState({ message: '', status: false })
  const handleUpdateProfile = useCallback(() => {
    updateProfileUser({ displayName, phoneNumber })
      .then((res) => {})
      .catch((error) => {
        setError((prevState) => ({
          ...prevState,
          message: error.message,
          status: true
        }))
      })
      .finally(() => {})
  }, [displayName, phoneNumber])

  return (
    <div className="pf__container">
      {error.status && <span>{error.message}</span>}
      <Card className={classes.root}>
        <div className="pf__avatar">
          <img src={avatar} alt="my-avatar" />
          <UploadImage buttonText="Upload avatar" />
        </div>
        <CardContent>
          <form noValidate autoComplete="off" style={{ display: 'flex', flexDirection: 'column' }}>
            <FormInput
              label="Email"
              type="email"
              name="email"
              defaultValue={userInfo && userInfo.email}
              disabled={true}
            />
            <FormInput
              label="UserName"
              type="text"
              name="displayName"
              defaultValue={userInfo && userInfo.username}
              setData={setDisplayName}
            />
            <FormInput
              label="Phone"
              type="phone"
              name="phoneNumber"
              defaultValue={(userInfo && userInfo.phone) || ''}
              setData={setPhoneNumber}
            />
          </form>
        </CardContent>
        <CardActions className="pf__actions">
          <Button size="small" variant="contained" color="primary" onClick={handleUpdateProfile}>
            Update profile
          </Button>
        </CardActions>
      </Card>
    </div>
  )
}

export default UserProfile
