import React from "react";
import PropTypes from "prop-types";
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/date-fns";
import localeJa from "date-fns/locale/ja";
import { findWeekDay } from "constants/utilities";

class CustomDateUtils extends DateFnsUtils {
  getYearText(date) {
    return date.getFullYear() + "年";
  }

  getCalendarHeaderText(date) {
    return date.getFullYear() + "年" + (date.getMonth() + 1) + "月";
  }

  getDatePickerHeaderText(date) {
    const result = `${date.getMonth() + 1}月${date.getDate()}日(${findWeekDay(
      date.getFullYear(),
      date.getMonth() + 1,
      date.getDate()
    )})`;
    return result;
  }
}

function CustomCalendar({ date, setDate, label }) {
  const onSelectDate = (date) => {
    setDate(date);
  };
  return (
    <MuiPickersUtilsProvider locale={localeJa} utils={CustomDateUtils}>
      <KeyboardDatePicker
        margin="normal"
        label={label}
        format="yyyy-MM-dd"
        value={date}
        onChange={onSelectDate}
        KeyboardButtonProps={{
          "aria-label": "change date",
        }}
      />
    </MuiPickersUtilsProvider>
  );
}

CustomCalendar.propTypes = {
  date: PropTypes.string,
  setDate: PropTypes.func,
  label: PropTypes.string,
};

export default CustomCalendar;
