import React, { useState, useEffect } from 'react'
import './Blog.css'
import postService from 'services/postService'
import FormInputTextArea from 'components/common/input/FormInputTextArea'
import { Button } from '@material-ui/core'

function PostDetail() {
  const currentUrl = window.location.href
  const postId = currentUrl.substring(currentUrl.lastIndexOf('/') + 1)
  const [data, setData] = useState({ post: {} })
  const [error, setError] = useState({ message: '', status: '' })
  console.log(data, error)
  useEffect(() => {
    postService
      .getPostDetail(postId)
      .then((data) => {
        if (data) {
          setData((prevState) => ({
            ...prevState,
            post: data
          }))
        }
      })
      .catch((error) => {
        setError((prevState) => ({
          ...prevState,
          message: error.message,
          status: error.status
        }))
      })
      .finally(() => {
        console.log('Finished request')
      })
  }, [postId])
  return (
    <div className="pd__container">
      <div className="pd__header">
        <h3>Post detail</h3>
        <ul>
          <li>
            <i className="fas fa-user-circle"></i>
            <span>Author: </span>
          </li>
          <li>
            <i className="far fa-calendar-alt"></i>
            <span> CreatedAt: </span>
          </li>
          <li>
            <i className="fas fa-clock"></i>
            <span>Hours</span>
          </li>
        </ul>
      </div>
      <div className="pd__body"></div>
      <div className="pd__reply">
        <h4>Leave a reply</h4>
        <form>
          <FormInputTextArea title="Comment" rowsMin={20} rowsMax={1000} />
          <Button>Post Comment</Button>
        </form>
      </div>
    </div>
  )
}

export default PostDetail
