import React, { useState, useEffect, useCallback } from 'react'
import './Login.css'
import FormInput from 'components/common/input/FormInput'
import { Card, CardHeader, CardContent, CardActions, Button } from '@material-ui/core'
import { useDispatch, useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { login } from 'redux/actions/userAction'

function Login() {
  const dispatch = useDispatch()
  const history = useHistory()
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const { isLogin } = useSelector((state) => state.userReducers)

  // Handle when click login button
  const handleLogin = useCallback(() => {
    dispatch(login.loginRequest({ email, password }))
  }, [email, password, dispatch])

  // If login successfully, redirect to home page
  useEffect(() => {
    if (isLogin) {
      history.push('/')
    }
  }, [isLogin, history])

  return (
    <div className="login">
      <Card>
        <CardHeader title="Login" />
        <CardContent>
          <form>
            <FormInput label="Email" setData={setEmail} />
            <FormInput label="Password" setData={setPassword} type="password" />
          </form>
        </CardContent>
        <CardActions>
          <Button color="primary" size="large" onClick={handleLogin}>
            Login
          </Button>
        </CardActions>
      </Card>
    </div>
  )
}

export default Login
